        $expiry_alert_days    = $this->Settings->expiry_alert_days;
        $settings_expiry_date = (!empty($expiry_alert_days) && $expiry_alert_days != '' && $expiry_alert_days !== 0) ? date('Y-m-d', strtotime(" +{$expiry_alert_days} days ")) : null;

