                            $row1++;
                            $row2++;
                            $row3++;
                        }
                        $rows++;
                    }
                    $test = $rows;
                    $this->excel->getActiveSheet()->mergeCells('A'.$test.':C'.$test);
                    $this->excel->getActiveSheet()->mergeCells('D'.$test.':G'.$test)->setCellValue('D'. $test , 'Ending Account Balance: ');

