                    $this->excel->getActiveSheet()->getStyle('F'.$test.':G'.$test)->getAlignment()->setHorizontal(PHPExcel_Style_Alignment::HORIZONTAL_RIGHT);
                    $row = $rows;
                    $rows = $rows + 2 ;
                    $this->excel->getActiveSheet()->getStyle('A'.$row.':J'.$row)->applyFromArray($BoStyle); 
                    $row++;
                }       
                
            }
            $BorStyle = array(
                      'borders' => array(
