                    'total_returns'   => $total_returns,
                    'total_expenses'  => $total_expenses,
                    'total_discounts' => $total_discounts->total_amount,
                    'total_returned'  => $total_return_warehouse->total_return,   
                    'total_cost'      => $total_cost->cost
