		if ($this->form_validation->run() == true) {
			$grades = $this->schools_model->getGradeByIDs($post['grade']);
			$subjects = $this->schools_model->getSubjectByIDs($post['subject']);
			$l = 0;
			$grade_name = '';
			if($grades){
				foreach($grades as $grade){
					if($l==0){
						$grade_name = $grade->name;
						$l = 1;
					}else{
						$grade_name .= ','.$grade->name;
					}
				}
			}
			
			$s = 0;
