				foreach($subjects as $subject){
					if($s==0){
						$subject_name = $subject->name;
						$s = 1;
					}else{
						$subject_name .= ','.$subject->name;
					}
				}
			}
			
			$data = array(
				'credit'  => $post['credit'],
				'program_id' => $post['program'],
				'grade_id' => json_encode($post['grade']),
