            . lang('r_u_sure') . "</p><a class='btn btn-danger' href='" . admin_url('schools/delete_attendance/$1') . "'>"
            . lang('i_m_sure') . "</a><button class='btn'>" . lang('no') . "</button>\"  rel='popover'><i class=\"fa fa-trash-o\"></i> "
            . lang('delete_attendance') . "</a>";
		
