		if ($xls) {
			// IF(".$this->db->dbprefix('sh_student_statuses').".status = 'reconfirm' OR ".$this->db->dbprefix('sh_student_statuses').".status = 'suspend',".$this->db->dbprefix('sh_student_statuses').".review_status, 'completed') as review_status,
