			$scholarship_id = $this->input->post('scholarship');
			$i = isset($_POST['study_info_id']) ? sizeof($_POST['study_info_id']) : 0;
			for ($r = 0; $r < $i; $r++) {
            	$data[] = $_POST['study_info_id'][$r];
            }
